SELECT COUNT(username)
FROM cherrypick.member


	WHERE username LIKE CONCAT('%', #{keyword}, '%');



	WHERE name LIKE CONCAT('%', #{keyword}, '%');



	WHERE phone LIKE CONCAT('%', #{keyword}, '%');


